from Crypto.Util.number import getPrime, isPrime, bytes_to_long
from secret import flag, e, p, q

alphabet = "0123456789abc"

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def _int_to_13(x: int):
    res = []
    while x > 0:
        res.append(alphabet[x % 13])
        x //= 13
    return ''.join(res[::-1])

def _13_to_int(s: str):
    return int(s, 13)

def genkey(bits):
    while True:
        p = getPrime(bits)
        q = _13_to_int(_int_to_13(p)[::-1])
        if isPrime(q):
            e = 65537
            while True:
                if gcd(e, (p - 1) * (q - 1)) == 1:
                    return e, p, q
                e += 1
                
e, p, q = genkey(1024)
